list = []can = NoneposList = []sca = Nonedef InitLayout(canvas, starList, originX, originY, scale) :    import Vector    import Star    import Tkinter    import math    global list    global can    global posList    global sca    can = canvas    sca = scale    for item in list:        canvas.delete(item[0])    list = []    for star in starList:        pos = star.getPos()        x = pos.getX()        y = pos.getY()        posList = posList + [(x,y)]        r = max((star.getR(), 1 / scale))        color = star.getColor()        id = canvas.create_oval(originX + (pos.getX() - r) * scale, \                              originY + (pos.getY() - r) * scale, \                              originX + (pos.getX() + r) * scale, \                              originY + (pos.getY() + r) * scale, \                              width = 0, fill=color)        list.append((id, star ))        